mapper.delete:

delete from energy_goal
where goal_start_date = #{goalStartDate,jdbcType=TIMESTAMP}
  and goal_end_date = #{goalEndDate,jdbcType=TIMESTAMP}
  and energy_type_id = #{energyTypeId,jdbcType=VARCHAR}

