  )
values (#{goalStartDate,jdbcType=TIMESTAMP}, #{goalEndDate,jdbcType=TIMESTAMP}, #{energyTypeId,jdbcType=VARCHAR}, 
  #{goalValue,jdbcType=INTEGER}, #{unitId,jdbcType=VARCHAR}, #{createdAt,jdbcType=TIMESTAMP}, 
  #{createdBy,jdbcType=VARCHAR}, #{updateAt,jdbcType=TIMESTAMP}, #{updatedBy,jdbcType=VARCHAR}
  )

mapper.update:

update energy_goal
set goal_value = #{goalValue,jdbcType=INTEGER},
  unit_id = #{unitId,jdbcType=VARCHAR},
  created_at = #{createdAt,jdbcType=TIMESTAMP},
  created_by = #{createdBy,jdbcType=VARCHAR},
  update_at = #{updateAt,jdbcType=TIMESTAMP},
  updated_by = #{updatedBy,jdbcType=VARCHAR}
where goal_start_date = #{goalStartDate,jdbcType=TIMESTAMP}
  and goal_end_date = #{goalEndDate,jdbcType=TIMESTAMP}
  and energy_type_id = #{energyTypeId,jdbcType=VARCHAR}

mapper.select:

