mapper.delete:

delete from energy_unit_price
where adapt_start_date = #{adaptStartDate,jdbcType=TIMESTAMP}
  and energy_type_id = #{energyTypeId,jdbcType=VARCHAR}

mapper.select:

select adapt_start_date, energy_type_id, adapt_end_date, base_value, unit_id, unit_price, 
created_at, created_by, updated_at, updated_by
from energy_unit_price
where adapt_start_date = #{adaptStartDate,jdbcType=TIMESTAMP}
  and energy_type_id = #{energyTypeId,jdbcType=VARCHAR}
