mapper.delete:

delete from energy_usage
where measure_date = #{measureDate,jdbcType=TIMESTAMP}
  and tag_id = #{tagId,jdbcType=VARCHAR}

mapper.update:

update energy_usage
set usage_value = #{usageValue,jdbcType=DECIMAL},
  adapt_unit_price = #{adaptUnitPrice,jdbcType=DECIMAL},
  energy_cost = #{energyCost,jdbcType=DECIMAL}
where measure_date = #{measureDate,jdbcType=TIMESTAMP}
  and tag_id = #{tagId,jdbcType=VARCHAR}

mapper.select:

select measure_date, tag_id, usage_value, adapt_unit_price, energy_cost
from energy_usage
where measure_date = #{measureDate,jdbcType=TIMESTAMP}
  and tag_id = #{tagId,jdbcType=VARCHAR}

    SELECT sum(usage_value) as tagUsage FROM paper_dt.energy_usage A, paper_dt.tag B
		WHERE A.tag_id = B.tag_id
		AND A.measure_date BETWEEN #{tagFrom,jdbcType=VARCHAR} AND #{tagTo,jdbcType=VARCHAR}
		AND B.energy_type_id = #{tagEnergyType,jdbcType=VARCHAR};

  SELECT sum(A.usage_value) as tagUsage, DATE_FORMAT(A.measure_date, "%Y-%m-%d") as tagDate FROM paper_dt.energy_usage A, paper_dt.tag B
		WHERE A.tag_id = B.tag_id
		AND A.measure_date BETWEEN CONCAT(DATE_FORMAT(CURDATE(), '%Y-%m-01')," " , "00:00:00") AND #{tagTo,jdbcType=VARCHAR}
		AND B.energy_type_id = #{tagEnergyType,jdbcType=VARCHAR}
		group by tagDate order by tagUsage DESC limit 1;
