mapper.insert:

insert into process (process_id, process_name, process_start_date, 
  process_end_date, process_type, factory_equipment_id, 
  process_status, created_at, created_by, 
  updated_at, updated_by, product_id
  )
values (#{processId,jdbcType=VARCHAR}, #{processName,jdbcType=VARCHAR}, #{processStartDate,jdbcType=TIMESTAMP}, 
  #{processEndDate,jdbcType=VARCHAR}, #{processType,jdbcType=VARCHAR}, #{factoryEquipmentId,jdbcType=VARCHAR}, 
  #{processStatus,jdbcType=VARCHAR}, #{createdAt,jdbcType=TIMESTAMP}, #{createdBy,jdbcType=VARCHAR}, 
  #{updatedAt,jdbcType=TIMESTAMP}, #{updatedBy,jdbcType=VARCHAR}, #{productId,jdbcType=VARCHAR}
  )

mapper.select:
    SELECT B.weight, sum(TIMESTAMPDIFF(minute, A.process_start_date, process_end_date)/60) as result
		FROM paper_dt.process A, paper_dt.product B
		WHERE A.product_id = B.product_id
		AND A.process_type = 'P'
		AND B.product_type_id = #{typeId,jdbcType=VARCHAR}
		AND A.process_start_date BETWEEN #{tagFrom,jdbcType=VARCHAR} AND #{tagTo,jdbcType=VARCHAR}
		AND A.process_end_date BETWEEN #{tagFrom,jdbcType=VARCHAR} AND #{tagTo,jdbcType=VARCHAR}
		GROUP BY B.weight

    SELECT B.weight, sum(TIMESTAMPDIFF(minute, A.process_start_date, process_end_date)/60) as result
		FROM paper_dt.process A, paper_dt.product B
		WHERE A.product_id = B.product_id
		AND A.process_type = 'W'
		AND B.product_type_id = #{typeId,jdbcType=VARCHAR}
		AND A.process_start_date BETWEEN #{tagFrom,jdbcType=VARCHAR} AND #{tagTo,jdbcType=VARCHAR}
		AND A.process_end_date BETWEEN #{tagFrom,jdbcType=VARCHAR} AND #{tagTo,jdbcType=VARCHAR}
		GROUP BY B.weight
