mapper.delete:

delete from process_worker
where process_id = #{processId,jdbcType=VARCHAR}
  and worker_id = #{workerId,jdbcType=VARCHAR}

mapper.insert:

insert into process_worker (process_id, worker_id, created_at, 
  created_by, updated_at, updated_by
  )
values (#{processId,jdbcType=VARCHAR}, #{workerId,jdbcType=VARCHAR}, #{createdAt,jdbcType=TIMESTAMP}, 
  #{createdBy,jdbcType=VARCHAR}, #{updatedAt,jdbcType=TIMESTAMP}, #{updatedBy,jdbcType=VARCHAR}
  )

mapper.update:

update process_worker
set created_at = #{createdAt,jdbcType=TIMESTAMP},
  created_by = #{createdBy,jdbcType=VARCHAR},
  updated_at = #{updatedAt,jdbcType=TIMESTAMP},
  updated_by = #{updatedBy,jdbcType=VARCHAR}
where process_id = #{processId,jdbcType=VARCHAR}
  and worker_id = #{workerId,jdbcType=VARCHAR}

mapper.select:

select process_id, worker_id, created_at, created_by, updated_at, updated_by
from process_worker
where process_id = #{processId,jdbcType=VARCHAR}
  and worker_id = #{workerId,jdbcType=VARCHAR}
